import styles from './cssRoutes/info.module.css'

import { useEffect, useState } from 'react';

import UpdateForm from '../components/dashboard/updateForm'
import Input from '../components/form/input'
import Select from '../components/form/select'

function Info(){
    const [user, setUser] = useState([])
    const [sexo, setSexo] = useState([])
    const [newPass, setNewPass] = useState([])
    const [message, setMessage] = useState("")

    const regexEmail = /\w+([-+.']\w+)*@\w+([-.]\w+)*\.\w+([-.]\w+)*/;
    const regexPass = /^(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])([a-zA-Z0-9]{8,})$/;

    const id = localStorage.getItem("id")

    useEffect(() =>{
        fetch(`http://localhost:5000/users/${id}`, {
            method: "GET",
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then((resp) => resp.json())
            .then((data) => {
                setUser(data)
            })
            .catch(err => console.log(err))
    },[id])

    useEffect(() => {
        fetch("http://localhost:5000/sexos", {
            method: "GET",
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then((resp) => resp.json())
            .then((data) => {
                setSexo(data)
            })
            .catch(err => console.log(err))
    }, [])

    function OnChangePass(e) {
        setNewPass({...newPass,[e.target.name]:e.target.value})
    }

    function OnChange(e){
        setUser({ ...user, [e.target.name]: e.target.value })
        if (e.target.value === '') {
            return false
        }
    }

    function handleSexo(e) {
        setUser({
            ...user, user_sexo: {
                id: e.target.value,
                name: e.target.options[e.target.selectedIndex].text
            }
        })
    }

    function submitPerson(e) {
        e.preventDefault()
    }

    return (
        <div className={styles.infoContainer}>
            <UpdateForm 
                formName="Dados Pessoais" 
                txtBtn="Atualizar"
                handleSubmit={submitPerson}
            >
                <Input
                    type="text"
                    text="Nome"
                    nameInput="user_name"
                    placeholder="Digite seu nome"
                    value={user.user_name ? user.user_name : ''}
                    handleOnChange={OnChange}
                />
                <Input
                    type="text"
                    text="Sobrenome"
                    nameInput="user_sbname"
                    placeholder="Digite seu sobrenome"
                    value={user.user_sbname ? user.user_sbname : ''}
                    handleOnChange={OnChange}
                />
                <Input
                    type="number"
                    text="Idade"
                    nameInput="user_age"
                    placeholder="Digite sua idade"
                    value={user.user_age ? user.user_age : ''}
                    handleOnChange={OnChange}
                />
                <Select
                    nameSelect="user_sexo"
                    options={sexo}
                    text="Sexo"
                    value={localStorage.getItem("idSexo")}
                    handleOnChange={handleSexo}
                />
            </UpdateForm>
            <UpdateForm formName="Dados da conta" txtBtn="Atualizar">
                <Input
                    type="email"
                    text="Email"
                    nameInput="user_email"
                    placeholder="Digite seu novo email"
                    value={user.user_email ? user.user_email : ''}
                    handleOnChange={OnChange}
                />
                <Input
                    type="password"
                    text="Antiga senha"
                    nameInput="user_passAn"
                    placeholder="Digite sua senha atual"
                    handleOnChange={OnChangePass}
                />
                <Input
                    type="password"
                    text="Nova senha"
                    nameInput="user_passNew"
                    placeholder="Digite sua nova senha"
                    handleOnChange={OnChangePass}
                />
                <Input
                    type="password"
                    text="Confirmar senha"
                    nameInput="conf_user_passNew"
                    placeholder="Confirme sua nova senha"
                    handleOnChange={OnChangePass}
                />
            </UpdateForm>
            <UpdateForm formName="Dados do responsável" txtBtn="Atualizar">

            </UpdateForm>
        </div>
    )
}

export default Info
