import styles from './cssForms/forms.module.css'

import {useState, useEffect} from 'react'

import ButtonForm from '../form/buttonForm'
import Input from '../form/input'

function FormAccount({handleSubmit, txtBtn}){
    const [user, setUser] = useState([])
    const [newPass, setNewPass] = useState([])
    const [message, setMessage] = useState("")
    const [type, setType] = useState("")
    const id = localStorage.getItem("id")

    useEffect(() =>{
        fetch(`http://localhost:5000/users/${id}`, {
            method: "GET",
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then((resp) => resp.json())
            .then((data) => {
                setUser(data)
            })
            .catch(err => console.log(err))
    },[id])
    
    function validateForm() {
        if(user.user_email == ''){
            console.log('ta vazio!')
        }
        if(user.user_pass != newPass.user_passAn){
            console.log("ta diferente")
        }
    }

    function OnChange(e) {
        
    }

    function OnChangePass(e) {
        setNewPass({...newPass,[e.target.name]:e.target.value})
    }

    function submit(e){
        e.preventDefault()
        validateForm()
    }

    return (
        <div className={styles.formContainer}>
            <h2>Dados da Conta</h2>
            <form onSubmit={submit}>
                <Input
                    type="email"
                    text="Email"
                    nameInput="user_email"
                    placeholder="Digite um novo email"
                    value={user.user_email ? user.user_email : ''}
                    handleOnChange={OnChangePass}
                />
                <Input
                    type="password"
                    text="Antiga senha"
                    nameInput="user_passAn"
                    placeholder="Digite sua senha atual"
                    handleOnChange={OnChangePass}
                />
                <Input
                    type="password"
                    text="Nova senha"
                    nameInput="user_passNew"
                    placeholder="Digite sua nova senha"
                    handleOnChange={OnChangePass}
                />
                <Input
                    type="password"
                    text="Confirmar senha"
                    nameInput="conf_user_passNew"
                    placeholder="Confirme sua nova senha"
                    handleOnChange={OnChangePass}
                />

                <div className={styles.btnContainer}>
                    <ButtonForm text={txtBtn}/>
                </div>
                
            </form>
        </div>
    )
}

export default FormAccount
