import styles from './cssRoutes/info.module.css'

import { useEffect, useState } from 'react';

import FormPerson from '../components/dashForms/formPerson'
import FormAccount from '../components/dashForms/formAccount'

function Info(){
    const [user, setUser] = useState([])
    const [message, setMessage] = useState("")

    const id = localStorage.getItem("id")

    useEffect(() =>{
        fetch(`http://localhost:5000/users/${id}`, {
            method: "GET",
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then((resp) => resp.json())
            .then((data) => {
                setUser(data)
            })
            .catch(err => console.log(err))
    },[id])

    function validatePerson(user) {
        if (user.length === 0) {
            setMessage('É necessário preencher todos os campos!')
            return false
        }
        if (user.user_name === '' || 
            user.user_sbname === '' || 
            user.user_age === ''
        ) {
            setMessage('É necessário preencher todos os campos!')
            return false
        }
        if (parseInt(user.user_age) < 5) {
            setMessage('A idade mínima é de 5 anos!')
            return false
        }
        if (user.user_sexo === undefined || user.user_sexo.id === 'Selecione uma opção') {
            setMessage('É necessário selecionar uma das opções!')
            return false
        }

        setMessage('')
        return fetchPerson()
    }

    function fetchPerson() {
        fetch(`http://localhost:5000/users/${id}`,{
            method:"PATCH",
            headers:{
                'Content-Type':'application/json'
            },
            body: JSON.stringify(user)
        })
        .then(resp => resp.json())
        .then(data => {
            setUser(data)
        })
        .catch(err => console.log(err))
    }

    function submitPerson(e) {
        e.preventDefault()
        validatePerson(user)
    }

    return (
        <div className={styles.infoContainer}>
            <FormPerson 
                txtBtn="Atualizar"
                dataUser={user}
            />
            <FormAccount 
                txtBtn="Atualizar"
            />

        </div>
    )
}

export default Info
