import { useState, useEffect } from 'react'

import { useNavigate } from 'react-router-dom'

//Import Components
import Container from '../components/layout/container'
import LoginForm from '../components/loginForm/loginForm'
function Login() {
    const [allUsers, setAllUsers] = useState([])
    const navigate = useNavigate()
    
    const getusers = async () =>{
        try {
            const response = await axios.get("mysql://root:S9maMI9Su9PQB800ZSgY@containers-us-west-70.railway.app:7461/railway")
        } catch (error) {
            console.log(error)
        }
    }

    useEffect(() => {
        fetch("http://localhost:5000/users", {
            method:"GET",
            headers:{
                'Content-Type':'application/json'
            }
        })
            .then(resp => resp.json())
            .then(data => {
                setAllUsers(data)
            })
            .catch(err => console.log(err))
    }, [])

    function createSession(user){
        allUsers.map(allUser => {
            if(allUser.user_email === user.email && allUser.user_pass === user.pass){
                setLocalStorage("name", allUser.user_name)
                setLocalStorage("sbName", allUser.user_sbname)
                setLocalStorage("id", allUser.id)
                
                return navigate(`/dashboard/${allUser.user_name}`)
            }
        })
    }

    function setLocalStorage(key, value){
        return localStorage.setItem(key, value)
    }

    return (
        <Container customClass="center">
            <h1>Login</h1>
            <LoginForm btnText="Entrar" handleSubmit={createSession} />
        </Container>
    )
}

export default Login
