import styles from './cssRoutes/info.module.css'

import { useParams } from 'react-router-dom'
import { useEffect, useState } from "react";

import UpdateForm from '../components/dashboard/updateForm'
import Input from '../components/form/input'
import Select from '../components/form/select'

function Info(){
    const [sexo, setSexo] = useState([])
    const [user, setUser] = useState([])
    const id = localStorage.getItem("id")

    useEffect(() => {
        fetch(`http://localhost:5000/users/${id}`, {
            method:"GET",
            headers:{
                'Content-Type': 'application/json'
            }
        })
        .then(resp => resp.json())
        .then(data => {
            setUser(data)
        })
        .catch(err => console.log(err))

        fetch("http://localhost:5000/sexos", {
            method: "GET",
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then((resp) => resp.json())
            .then((data) => {
                setSexo(data)
            })
            .catch(err => console.log(err))
    }, [])
    
    console.log(user.user_sexo.id)
    return (
        <div className={styles.infoContainer}>
            <UpdateForm formName="Dados Pessoais" txtBtn="Atualizar">
                <Input
                    type="text"
                    text="Nome"
                    value={user.user_name}
                />
                <Input
                    type="text"
                    text="Sobrenome"
                    value={user.user_sbname}
                />
                <Input
                    type="number"
                    text="Idade"
                    value={user.user_age}
                />
                <Select
                    nameSelect="user_sexo"
                    options={sexo}
                    text="Sexo"
                />
            </UpdateForm>
            <UpdateForm formName="Dados da conta" txtBtn="Atualizar"/>
            <UpdateForm formName="Dados do responsÃ¡vel" txtBtn="Atualizar"/>
        </div>
    )
}

export default Info
