import styles from './cssRoutes/login.module.css'

import { useState, useEffect } from 'react'

//Import Components
import Container from '../components/layout/container'
import LoginForm from '../components/loginForm/loginForm'
function Login() {
    const [allUsers, setAllUsers] = useState([])
    let email = ""
    let pass = ""
    
    function createSession(user){
        fetch("http://localhost:5000/users", {
            method:"GET",
            headers:{
                'Content-Type':'application/json'
            }
        })
            .then(resp => resp.json())
            .then(data => {
                setAllUsers(data)
            })
            .catch(err => console.log(err))

        allUsers.map(allUser => {
            if(allUser.user_email === user.email && allUser.user_pass === user.pass){
                localStorage.setItem("username", allUser.user_name)
            }
        })
    }

    return (
        <Container customClass="center">
            <h1>Login</h1>
            <LoginForm btnText="Entrar" handleSubmit={createSession}/>
        </Container>
    )
}

export default Login
