import styles from './formStyles/select.module.css'

function Select({nameSelect, text, options, value}){
    return(
        <div className={styles.selectContainer}>
            <label htmlFor={nameSelect}>{text}</label>
            <select name={nameSelect} id={nameSelect} value={value || ''}>
                <option > Selecione um opção </option>
                {
                    options.map((option) =>{
                        <option value={option.id} key={option.id}></option>
                    })
                }
            </select>
        </div>
    )
}

export default Select
