import styles from './cssLogin/loginForm.module.css'

//Import react-doms
import { useLocation } from 'react-router-dom'

import { useState, useEffect } from 'react'

//Import Components
import Input from '../form/input'
import Checkbox from '../form/checkbox'
import ButtonForm from '../form/buttonForm'
import Message from '../layout/message'

import { Link } from 'react-router-dom'

function LoginForm({handleSubmit, btnText, userData}) {
    const [user, setUser] = useState(userData || [])
    const [allUsers, setAllUsers] = useState([])
    const location = useLocation()
    let message = ''

    useEffect(()=>{
        fetch("http://localhost:5000/users", {
            method:'GET',
            headers:{
                'Content-Type':'application/json'
            }
        })
            .then(resp => resp.json())
            .then(data =>{
                setAllUsers(data)
            })
            .catch(err => console.log(err))
    }, [])

    function validationForm(user){

    }

    const submit = (e) =>{
        e.preventDefault()
        validationForm(user)
    }

    function handleSubmit(e){
        setUser()
    }

    if(location.state){
        message = location.state
    }

    return (
        <div className={styles.formContainer}>
            {message && (
                <Message msg={message} type="success"/>
            )}
            <form onSubmit={submit}>
                <Input
                    type="text"
                    text="Email"
                    nameInput="email"
                    placeholder="Digite seu email"
                    handleOnChange={handleSubmit}
                />
                <Input
                    type="password"
                    text="Senha"
                    nameInput="senha"
                    placeholder="Digite sua senha"
                    handleOnChange={handleSubmit}
                />
                <Checkbox 
                    text="Manter-me conectado"
                    nameCheckbox="connect"
                    value="connect"
                />
                <ButtonForm text={btnText} />
            </form>
            <div className={styles.linkContainer}>
                <Link to="/register"> NÃ£o possui um cadastro?<br/>Registre-se! </Link>
            </div>
        </div>
    )
}

export default LoginForm
